mapper.select:
   		SELECT COUNT(*) 
		  FROM BOARD
          JOIN CATEGORY USING (CATEGORY_NO)
		 WHERE STATUS = 'Y'

           AND MAINCATEGORY_NAME = #{mainCategory}


		   AND CATEGORY_NAME = #{categoryName}

SELECT * FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT B.BOARD_NO, B.BOARD_TITLE, B.COST, G.GRADE_NAME, B.BOARD_WRITER, A.FILE_PATH || A.CHANGE_NAME "TITLEIMG" FROM BOARD B, CATEGORY C, ATTACHMENT A, SELLER S, GRADE G WHERE B.CATEGORY_NO = C.CATEGORY_NO AND B.BOARD_NO = A.BOARD_NO AND B.SELLER_NO = S.SELLER_NO AND S.GRADE_NO = G.GRADE_NO AND FILE_LEVEL = 1 AND B.STATUS = 'Y'
AND MAINCATEGORY_NAME = #{mainCategory}
AND CATEGORY_NAME = #{categoryName}
AND ${type} LIKE '%' || #{keyword} || '%'
ORDER BY ${col} ${order}) A) WHERE RNUM BETWEEN ${startRow} AND ${endRow}

SELECT COUNT(*) 
  FROM SELLER
 WHERE MEMBER_NO = ${memberNo}

mapper.update:
UPDATE BOARD
   SET COUNT = COUNT + 1
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT CATEGORY_NO
  FROM CATEGORY
 WHERE MAINCATEGORY_NAME = #{mainCategory}
   AND CATEGORY_NAME = #{categoryName}

mapper.update:
UPDATE BOARD
   SET CATEGORY_NO = #{categoryNo}
     , BOARD_TITLE = #{boardTitle}
     , LESSON_CONTENT = #{lessonContent}
     , CAREER = #{career}
     , BOARD_CONTENT = #{boardContent}
     , COST = #{cost}
     , ST_DATE = #{stDate}
     , FN_DATE = #{fnDate}
 WHERE BOARD_NO = #{boardNo}

UPDATE ATTACHMENT
   SET BOARD_NO = #{boardNo}
     ,ORIGIN_NAME = #{originName}
   	 ,CHANGE_NAME = #{changeName}
   	 ,FILE_PATH = #{filePath}
   	 ,FILE_SIZE =  #{fileSize}
   	 ,FILE_TYPE = #{fileType}
   	 ,UPLOAD_DATE = SYSDATE
   	 ,FILE_LEVEL = #{fileLevel}
   	 ,STATUS = #{status}
 WHERE FILE_NO = #{fileNo}

UPDATE BOARD
   SET STATUS = 'N'
 WHERE BOARD_NO = ${ boardNo }

mapper.select:
SELECT SELLER_NO
    FROM SELLER
   WHERE MEMBER_NO = ${memberNo}

SELECT *
  FROM SELLER
 WHERE SELLER_NO = ${ sellerNo }

SELECT MEMBER_NO
  FROM MEMBER
  WHERE MEMBER_NICK = #{boardWriter}
