mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
  AND MEMBER_PWD = #{memberPwd}
  AND STATUS = 'Y'

SELECT COUNT(*) 
FROM MEMBER 
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICK = #{memberNick}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_PHONE = #{memberPhone}

mapper.update:
UPDATE MEMBER
SET MEMBER_NICK = #{ memberNick }
WHERE MEMBER_NO = #{ memberNo }

UPDATE MEMBER
SET MEMBER_EMAIL = #{ memberEmail }
WHERE MEMBER_NO = #{ memberNo }

UPDATE SELLER
SET
CONTENTS = #{ contents },
FILE_PATH = #{ upfilePath }
WHERE MEMBER_NO = #{ memberNo }

UPDATE MEMBER
SET MEMBER_PHONE = #{ memberPhone }
WHERE 
MEMBER_NO = #{ memberNo }

UPDATE MEMBER
SET MEMBER_PWD = #{ memberNewPwd }
WHERE
MEMBER_NO = #{ memberNo }

mapper.select:
